import { CommonModule } from '@angular/common';
import { AfterViewInit, Component } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { RouterModule } from '@angular/router';
import { TranslateModule, TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, TranslateModule],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements AfterViewInit {
  private typingInterval: any; // Referência para o intervalo da animação

  constructor(private translate: TranslateService) {}

  ngAfterViewInit() {
    this.startTypingAnimation();
  }

  startTypingAnimation() {
    this.translate.get('SUBTITLE').subscribe((translatedText: string) => {
      this.typeText(translatedText, 'animated-text');
    });
  }

  private typeText(text: string, elementId: string) {
    const element = document.getElementById(elementId);
    let i = 0;

    if (element) {
      element.innerHTML = ''; // Limpa o texto existente
      clearInterval(this.typingInterval); // Garante que nenhuma animação anterior esteja ativa

      this.typingInterval = setInterval(() => {
        if (i < text.length) {
          element.innerHTML += text.charAt(i);
          i++;
        } else {
          clearInterval(this.typingInterval);
        }
      }, 100); // Velocidade da digitação
    }
  }

  switchLanguage(language: string) {
    this.translate.use(language).subscribe(() => {
      this.startTypingAnimation(); // Reinicia a animação com o novo idioma
    });
  }

  scrollToSection(sectionId: string) {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }
}
