import { CommonModule } from '@angular/common';
import { AfterViewInit, Component } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { Router, RouterModule } from '@angular/router';
import { TranslateModule, TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, TranslateModule, RouterModule],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements AfterViewInit {
  private typingInterval: any;

  constructor(private translate: TranslateService, private router: Router) {}

  ngAfterViewInit() {
    this.startTypingAnimation();
    // Ouvir mudanças de idioma globalmente
    this.translate.onLangChange.subscribe(() => {
      this.startTypingAnimation();
    });
  }

  startTypingAnimation() {
    this.translate.get('SUBTITLE').subscribe((translatedText: string) => {
      this.typeText(translatedText, 'animated-text');
    });
  }

  private typeText(text: string, elementId: string) {
    const element = document.getElementById(elementId);
    let i = 0;

    if (element) {
      element.innerHTML = ''; // Limpa o texto existente
      clearInterval(this.typingInterval); // Evita intervalos sobrepostos

      this.typingInterval = setInterval(() => {
        if (i < text.length) {
          element.innerHTML += text.charAt(i);
          i++;
        } else {
          clearInterval(this.typingInterval);
        }
      }, 100); // Velocidade da digitação
    }
  }

  scrollToSection(sectionId: string) {
    this.router.navigate(['/' + sectionId]).then(() => {
      setTimeout(() => {
        const element = document.getElementById(sectionId);
        if (element) {
          const yOffset = -80; // Ajuste para compensar o header fixo
          const yPosition = element.getBoundingClientRect().top + window.pageYOffset + yOffset;
          window.scrollTo({ top: yPosition, behavior: 'smooth' });
        }
      }, 100);
    });
  }
  
}
